// Support for importing SVGs
declare module "*.svg" {
  const content: any;
  export default content;
}

// Support for importing PNGs
declare module "*.png" {
  const content: any;
  export default content;
}

// Support for importing JPGs
declare module "*.jpg" {
  const content: any;
  export default content;
}

// Support for importing JPEGs
declare module "*.jpeg" {
  const content: any;
  export default content;
}
